import React from 'react'
import { useState, ueEffect } from 'react'
import { Widgets } from "@mui/icons-material"
import '../style/Nav.css'
import { useSpring, animated, config } from 'react-spring'
import Button from '@mui/material/Button';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';

/*
function Nav(){

    const [isOpen, setIsOpen] = useState(false)
    const props_menu = useSpring({ to: { opacity: 1 }, from: { opacity: -1 }, delay: 1000 })

    return isOpen ? (

        <div  className='navbar'>
            <animated.div style={props_menu}>

                <h2
                        className='navbar-toggle-title'
                        onClick={() => setIsOpen(false)}
                >
                        Fermer
                </h2>               
                    <div className='navbarMenu'>
                        <h2 className='navbarTitle'>Menu</h2>
                        <ul className='navbarList'>

                            <li className='navbarListItem active'>    
                                <Widgets className='navbarIcon'/>         
                                Widget1 
                            </li>
                            <li className='navbarListItem'>  
                            <Widgets className='navbarIcon' />                         
                            Widget2 
                            </li>
                            <li className='navbarListItem'>  
                                <Widgets className='navbarIcon' />                          
                                Widget3 
                            </li>
                            <li className='navbarListItem'>    
                                <Widgets className='navbarIcon' />                       
                                Widget4 
                            </li>
                            <li className='navbarListItem'> 
                                <Widgets className='navbarIcon' />                          
                                Widget5 
                            </li>
                            <li className='navbarListItem'> 
                                <Widgets className='navbarIcon' />                          
                                Widget6 
                            </li>
                        </ul>
                    </div>       
            </animated.div>            
        </div>

    ) : (

            <div className='navbar'>
                <h2
                    className='navbar-toggle-title'
                    onClick={() => setIsOpen(true)}
                >
                    Ouvrir le menu
                </h2>
            </div>

    )
}

export default Nav;*/

export default function Nav() {
    const history = useHistory();
    const location = useLocation();
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);

    return ( <
        React.Fragment > { /* Sidebar */ } <
        div className = { `fixed inset-y-0 left-0 z-30 w-64 overflow-y-auto transition duration-300 ease-out transform translate-x-0 bg-white border-r-2 lg:translate-x-0 lg:static lg:inset-0 ${
            isSidebarOpen ? "ease-out translate-x-0" : "ease-in -translate-x-full"
          }` } >
        <
        div className = "flex items-center justify-center mt-10 text-center py-6" >
        <
        span className = "mx-2 text-2xl font-semibold text-black" >
        react - minimal - side - navigation <
        /span> <
        /div>

        { /* https://github.com/abhijithvijayan/react-minimal-side-navigation */ } <
        Navigation activeItemId = { location.pathname }
        onSelect = {
            ({ itemId }) => {
                history.push(itemId);
            }
        }
        items = {
            [{
                    title: "Home",
                    itemId: "/home",
                    // Optional
                    elemBefore: () => < Icon name = "coffee" / >
                },
                {
                    title: "About",
                    itemId: "/about",
                    elemBefore: () => < Icon name = "user" / > ,
                    subNav: [{
                            title: "Projects",
                            itemId: "/about/projects",
                            // Optional
                            elemBefore: () => < Icon name = "cloud-snow" / >
                        },
                        {
                            title: "Members",
                            itemId: "/about/members",
                            elemBefore: () => < Icon name = "coffee" / >
                        }
                    ]
                },
                {
                    title: "Another Tab",
                    itemId: "/another",
                    subNav: [{
                        title: "Teams",
                        itemId: "/another/teams"
                            // Optional
                            // elemBefore: () => <Icon name="calendar" />
                    }]
                }
            ]
        }
        />

        <
        div className = "absolute bottom-0 w-full my-8" >
        <
        Navigation activeItemId = { location.pathname }
        items = {
            [{
                title: "Settings",
                itemId: "/settings",
                elemBefore: () => < Icon name = "activity" / >
            }]
        }
        onSelect = {
            ({ itemId }) => {
                history.push(itemId);
            }
        }
        /> <
        /div> <
        /div> <
        /React.Fragment>
    );
};
